import React, { useEffect, useState } from "react";
import API from "../api";
import Shortener from "./Shortener";

const Dashboard = () => {
  const [urls, setUrls] = useState([]);

  const fetchUrls = async () => {
    try {
      const { data } = await API.get("/url");
      setUrls(data);
    } catch (err) {
      alert(err.response?.data?.message || "Error fetching URLs");
    }
  };

  useEffect(() => {
    fetchUrls();
  }, []);

  return (
    <div className="container mt-5">
      <div className="card shadow-lg border-0 animate__animated animate__fadeInUp">
        <div className="card-body">
          <h2 className="text-center mb-4 text-danger">ðŸ“Š Your Dashboard</h2>
          <Shortener fetchUrls={fetchUrls} />
          <table className="table table-hover table-bordered align-middle shadow-sm">
            <thead className="table-dark">
              <tr>
                <th>Original URL</th>
                <th>Short ID</th>
                <th>Clicks</th>
              </tr>
            </thead>
            <tbody>
              {urls.map((u) => (
                <tr key={u._id} className="animate__animated animate__fadeIn">
                  <td><a href={u.originalUrl} target="_blank" rel="noreferrer">{u.originalUrl}</a></td>
                  <td><a href={`http://localhost:5000/api/url/${u.shortId}`} target="_blank" rel="noreferrer">{u.shortId}</a></td>
                  <td><span className="badge bg-success">{u.clicks}</span></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
