import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { logout, getToken } from "../auth";

const Navbar = () => {
  const navigate = useNavigate();
  const isLoggedIn = !!getToken();

  return (
    <nav
      className="navbar navbar-expand-lg navbar-dark shadow-lg p-3"
      style={{ background: "linear-gradient(90deg, #ff6a00, #ee0979)" }}
    >
      <div className="container-fluid">
        <Link className="navbar-brand fw-bold text-white animate__animated animate__pulse animate__infinite" to="/">
          ðŸš€ URL Shortener
        </Link>
        <div className="collapse navbar-collapse">
          <ul className="navbar-nav ms-auto">
            {isLoggedIn ? (
              <>
              
                <li className="nav-item">
                  <button
                    className="btn btn-light btn-sm ms-2 shadow-sm"
                    onClick={() => {
                      logout();
                      navigate("/login");
                    }}
                  >
                    Logout
                  </button>
                </li>
              </>
            ) : (
              <>
                <li className="nav-item">
                  <Link className="nav-link text-white" to="/login">
                    Login
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link text-white" to="/register">
                    Register
                  </Link>
                </li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
