import React, { useEffect, useState } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [urls, setUrls] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const fetchUrls = async () => {
    try {
      const { data } = await API.get("/url");
      setUrls(data);
    } catch (err) {
      console.error("❌ Fetch URLs error:", err.response?.data || err.message);
      setError("Failed to load URLs. Please log in again.");
    }
  };

  useEffect(() => {
    fetchUrls();
  }, []);

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fw-bold">📊 Your Dashboard</h2>
        {/* ✅ Go to Shortener button */}
        <button
          className="btn btn-success shadow-sm"
          onClick={() => navigate("/shortener")}
        >
          ➕ Go to Shortener
        </button>
      </div>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="card p-4 shadow">
        <h4 className="mb-3">Your Shortened URLs</h4>
        {urls.length === 0 ? (
          <p className="text-muted">No URLs shortened yet.</p>
        ) : (
          <table className="table table-striped table-bordered">
            <thead className="table-dark">
              <tr>
                <th>Original URL</th>
                <th>Short URL</th>
                <th>Clicks</th>
              </tr>
            </thead>
            <tbody>
              {urls.map((url) => (
                <tr key={url._id}>
                  <td>
                    <a href={url.originalUrl} target="_blank" rel="noreferrer">
                      {url.originalUrl}
                    </a>
                  </td>
                  <td>
                    <a
                      href={`http://localhost:5000/api/url/${url.shortId}`}
                      target="_blank"
                      rel="noreferrer"
                    >
                      {`http://localhost:5000/api/url/${url.shortId}`}
                    </a>
                  </td>
                  <td>{url.clicks}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
