import React, { useEffect, useState } from "react";
import API from "../api";
import Shortener from "./Shortener";

const Dashboard = () => {
  const [urls, setUrls] = useState([]);

  const fetchUrls = async () => {
    try {
      const { data } = await API.get("/url");
      setUrls(data);
    } catch (err) {
      alert(err.response?.data?.message || "Error fetching URLs");
    }
  };

  useEffect(() => {
    fetchUrls();
  }, []);

  return (
    <div className="container mt-5 animate__animated animate__fadeIn">
      <div
        className="card shadow-lg border-0"
        style={{
          background: "linear-gradient(135deg, #fdfbfb 0%, #ebedee 100%)",
        }}
      >
        <div className="card-body">
          <h2
            className="text-center fw-bold mb-4"
            style={{
              background: "linear-gradient(90deg, #ff512f, #dd2476)",
              WebkitBackgroundClip: "text",
              color: "transparent",
            }}
          >
            ðŸ“Š Your Dashboard
          </h2>

          {/* URL Shortener Form */}
          <Shortener fetchUrls={fetchUrls} />

          {/* Table of URLs */}
          <div className="table-responsive">
            <table className="table table-hover table-bordered align-middle shadow-sm">
              <thead
                className="text-white"
                style={{ background: "linear-gradient(90deg, #36d1dc, #5b86e5)" }}
              >
                <tr>
                  <th>Original URL</th>
                  <th>Short ID</th>
                  <th>Clicks</th>
                </tr>
              </thead>
              <tbody>
                {urls.length === 0 ? (
                  <tr>
                    <td colSpan="3" className="text-center text-muted py-3">
                      ðŸš« No URLs shortened yet. Try adding one!
                    </td>
                  </tr>
                ) : (
                  urls.map((u) => (
                    <tr
                      key={u._id}
                      className="animate__animated animate__fadeInUp"
                    >
                      <td>
                        <a
                          href={u.originalUrl}
                          target="_blank"
                          rel="noreferrer"
                          className="text-decoration-none fw-semibold"
                        >
                          {u.originalUrl}
                        </a>
                      </td>
                      <td>
                        <a
                          href={`http://localhost:5000/api/url/${u.shortId}`}
                          target="_blank"
                          rel="noreferrer"
                        >
                          <span
                            className="badge"
                            style={{
                              background:
                                "linear-gradient(90deg, #36d1dc, #5b86e5)",
                              fontSize: "1rem",
                            }}
                          >
                            {u.shortId}
                          </span>
                        </a>
                      </td>
                      <td>
                        <span className="badge bg-success fs-6 px-3 py-2">
                          {u.clicks}
                        </span>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
