import React, { useState } from "react";
import API from "../api";

const Shortener = ({ fetchUrls }) => {
  const [url, setUrl] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.post("/url/shorten", { originalUrl: url });
      setUrl("");
      fetchUrls();
    } catch (err) {
      alert(err.response?.data?.message || "Error shortening URL");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="d-flex my-3 animate__animated animate__fadeIn">
      <input
        className="form-control me-2 shadow-sm"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        placeholder="Enter URL"
      />
      <button className="btn btn-warning shadow-sm">Shorten</button>
    </form>
  );
};

export default Shortener;
