import React, { useState } from "react";
import API from "../api";
import { saveToken } from "../auth";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [form, setForm] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await API.post("/auth/login", form);
      saveToken(data.token);
      navigate("/dashboard");
    } catch (err) {
      alert(err.response?.data?.message || "Login failed");
    }
  };

  return (
    <div className="container mt-5">
      <div className="card shadow-lg border-0 animate__animated animate__zoomIn">
        <div className="card-body">
          <h2 className="text-center mb-4 text-success">üîê Login</h2>
          <form onSubmit={handleSubmit}>
            <input className="form-control my-2" name="email" placeholder="Email" onChange={handleChange} />
            <input className="form-control my-2" type="password" name="password" placeholder="Password" onChange={handleChange} />
            <button className="btn btn-primary w-100 mt-2">Login</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
